<?php defined('BASEPATH') OR exit('No direct script access allowed.');/** * * *  * Created by PhpStorm. *  * User: boonkhailim *  * Year: 2017 * */if (!function_exists('shuffleAssoc')) {	function shuffleAssoc($list) { 	  if (!is_array($list)) return $list; 	  $keys = array_keys($list); 	  shuffle($keys); 	  $random = array(); 	  foreach ($keys as $key) 	    $random[$key] = $list[$key]; 	  return $random; 	} }